import datetime

class Contact:
    def __init__(self, name, contact_number, email=None):
        self.name = name
        self.contact_number = contact_number
        self.email = email
        self.date_created = datetime.datetime.now().isoformat()

    def __repr__(self):
        return f"({self.name}, {self.contact_number}, {self.email}, {self.date_created})"
